import boxes from './boxes';
import React from 'react';
import Box from './Box.js'

function App() {

    const [squares,setSquares] = React.useState(boxes);
    console.log(squares)

   function toggle(id)  {
       setSquares(prestate => {
         return [
             {
                 prestate,
                 on:!prestate.on

             }
         ]
       })
   }

    const squareElements = squares.map(square => (

        <Box
            key={square.id}
            id={square.id}
            on={square.on}
            toggle={toggle}

        />

    ))


    return (
        <main>
            <h1>Boxes will go here</h1>
            {squareElements}
        </main>
    )
}

export default App;
