     {
         if(squares%2===0)
         {
             console.log("even")
         }
         else
         {
             console.log("odd")
         }
     }
 const style = {
     backgroundColor:props.darkMode ? "#222222" : "#cccccc"
 }

    const squareElements = squares.map(square => (

        <div style={style} className="box" key={square.id}></div>
    ))


